                <div class="card-body">
                    <div class="card col-sm-12">
                        <div class="card-header">
                            <h5 class="text-info">Consulta de Clientes</h5>
                        </div>
                        <div class="card-body">
                            <form id="formCliente">
                                <div class="row">
                                    <div class="mb-3 col-md-2">
                                        <label for="numeroCPF">CPF</label>
                                        <input type="text" class="form-control" id="numeroCPF" name="numeroCPF" placeholder="CPF" onkeypress="$(this).mask('000.000.000-00');">
                                    </div>
                                    <div class="mb-3 col-md-4">
                                        <label for="nomeCliente">Nome</label>
                                        <input type="text" class="form-control" id="nomeCliente" name="nomeCliente" placeholder="Nome">
                                    </div>
                                    <div class="mb-3 col-md-3">
                                        <label for="dataNascimento">Data de Nascimento</label>
                                        <input type="text" class="form-control" id="dataNascimento" name="dataNascimento" placeholder="Data de Nascimento" onkeypress="$(this).mask('00/00/0000')">
                                    </div>
                                    <div class="mb-3 col-md-3">
                                        <label for="sexoCliente">Sexo</label><br>
                                        <div class="form-check form-check-inline align-center">
                                            <input class="form-check-input" type="radio" name="sexoCliente" id="sexoCliente" value="M">
                                            <label class="form-check-label" for="sexoCliente">
                                                Masculino
                                            </label>
                                        </div>
                                        <div class="form-check form-check-inline">
                                            <input class="form-check-input" type="radio" name="sexoCliente" id="sexoCliente" value="F">
                                            <label class="form-check-label" for="sexoCliente">
                                                Feminino
                                            </label>
                                        </div>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="mb-3 col-md-6">
                                        <label for="estado_id">Estado</label>
                                        <select class="form-select" aria-label="select" id="estado_id" name="estado_id" onchange="buscaCidades(this)">
                                        </select>
                                    </div>
                                    <div class="mb-3 col-md-6">
                                        <label for="cidade_id">Cidade</label>
                                        <select class="form-select" aria-label="select" id="cidade_id" name="cidade_id">
                                            </select>
                                    </div>
                                </div>
                                <div class="row">
                                    <div class="mb-3 col-md-12" style="text-align: right;">
                                        <button type="button" class="btn btn-primary" onclick="pesquisarCliente()">
                                            Pesquisar Cliente
                                        </button>

                                        <button type="button" class="btn btn-light"  onclick="resetCliente()">
                                            Limpar Formulário
                                        </button>

                                        <button type="button" class="btn btn-warning" data-bs-toggle="modal" data-bs-target="#exampleModal" data-whatever="@Insert">
                                            Novo Cliente
                                        </button>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>